import React, { useState } from 'react'
import { Button, Col, Container, Row, Table } from 'reactstrap'
import Data from './Data';
import * as XLSX from 'xlsx'


function Upload_and_show_data() {
  const [data, setData] = useState(null);

  const [file, setFile] = useState(null);
  const [fileError, setfileError] = useState(null);

  console.log(file);
  
  //handle file
  const handleFile = (e) => { 
    let selectedFile = e.target.files[0];

    const fileType = ['application/vnd.openxmlformats-officedocument.spreadsheetml.sheet']


    if(selectedFile) {
      // console.log(selectedFile.type);
      if(selectedFile && fileType.includes(selectedFile.type)){
        let reader  = new FileReader();
        reader.readAsArrayBuffer(selectedFile);
        reader.onload=(e)=>{
          setfileError(null);
          setFile(e.target.result);
        }
      }else{
        setfileError('Please select correct file format');
        setFile(null);
      }
    }else{
      console.log("file not selected");
    }
   }

   const handleSubmit = (e) => { 
      e.preventDefault();
      if(file !== null){
        const workBook  = XLSX.read(file, {type: 'buffer'});
        const workSheetName = workBook.SheetNames[0];
      }else{
        setData = null;
      }
    }
  return (
    <div className='container'>
      <div className='form'>
      <form className='form-group' autoComplete='off' onSubmit={handleSubmit}>
        <label><h5>
          Upload Excel file
        </h5>
        <br/>
        <input type = "file" className='form-control' required onChange={handleFile}></input>
        <Button color="primary" type = 'submit'>
          Upload data!!
        </Button>
        </label>
        
        
      </form>
      </div>

      <Container fluid>
        <Row>
          <Col>
            <Table className = 'table-bordered' responsive bordered>
              <thead>
                <tr>
                  <th>S. No</th>
                  <th>Marks</th>
                  <th>Name</th>
                </tr>
              </thead>
              <tbody>
                <Data data = {data}/>
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>
    </div>
  )
}

export default Upload_and_show_data
